import React from 'react'
import Item from './Item'
import axios from 'axios'

const CardItem = (props) => {


  const onAddFavorites = async (obj) => {
    try{
        const findFavorites = props.favorites.find(objFavorites => objFavorites.myId === obj.myId)
        if (findFavorites){
          axios.delete(`https://637f91ca2f8f56e28e904e7d.mockapi.io/favorites/${findFavorites.id}`)
          props.setFavorites((over) => over.filter(item => item.myId !== obj.myId))

    }
    else{
        const {data} = await 
        axios.post(`https://637f91ca2f8f56e28e904e7d.mockapi.io/favorites`, obj)
        props.setFavorites([...props.favorites, data]);
    }
  }
    catch{
      alert('Произошла ошибка')
    }
  }

  const onAddBasket = async (obj) => {
    try{
        const findCart = props.cartItems.find(objCart => objCart.myId === obj.myId)
        if (findCart){
          axios.delete(`https://637f91ca2f8f56e28e904e7d.mockapi.io/cart/${findCart.id}`)
          props.setCartItems((over) => over.filter(item => item.myId !== obj.myId))

    }
    else{
        const {data} = await 
        axios.post(`https://637f91ca2f8f56e28e904e7d.mockapi.io/cart`, obj)
        props.setCartItems([...props.cartItems, data]);
    }
  }
    catch{
      alert('Произошла ошибка')
    }
  }

  return (
<div className='conteiner py-3'>

    { 
      props.item.map(obj=>{
        return(
           <Item
           key={obj.id}
           id={obj.id}
           myId={obj.myId}
           title={obj.title}
           description={obj.description}
           price={obj.price}
           img={obj.img}

           favoritesBtn={
            (favoritesObj)=>{
              onAddFavorites(favoritesObj)
            }
           }

           cartBtn={
            (cartObj)=>{
              onAddBasket(cartObj)
            }
           }
           />
        )
      })
    }
  </div>
  )
}

export default CardItem
