import axios from 'axios'
import React from 'react'
import {AppContext} from '../App'
import Item from './i'

const Favorites = (props) => {

  const context = React.useContext(AppContext)

  const onAddCart = (obj) => {
    axios.post(`https://637f91ca2f8f56e28e904e7d.mockapi.io/cart`, obj)
    context.setCartItems([...props.cartItems, obj]);
  }

const onDeleteFavorite = (id) => {
  axios.delete(`https://637f91ca2f8f56e28e904e7d.mockapi.io/favorite/${id}`)
  context.setFavorites((fav) => fav.filter(f => f.id !== id));
}


  return (
    <div>
      <div>
        <h1 className='col-md-8 offset-md-2'>Избранные туры</h1>
      </div>
    <div>
      {
        context.favorites.map(obj =>{
          return(
             <Item
             key={obj.id}
             id={obj.id}
             title={obj.title}
             price={obj.price}
             img={obj.img}

             onDeleteFavorite={
              (id)=>{
                onDeleteFavorite(id)
              }
             }

             cartBtn={
              (cartObj)=>{
                onAddCart(cartObj)
              }
             }


             />
          )
        })

      }

    </div>

    </div>

  )
}

export default Favorites
