import { Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from './components/Header';
import Footer from './components/Footer';

import "./App.css"
import { useState,useEffect} from 'react';
import axios from 'axios';
import Home from './components/Home';
import CartItem from './components/cart/CartItem';
import Form from './components/Form';
import {Route, Routes, BrowserRouter as Router} from 'react-router-dom'
import React from 'react';
import Favorites from './components/favorites/Favorites';
import Basket from './components/basket/Basket';

export const AppContext = React.createContext({})

function App() {
//хранения данных туров
const [tyrs, setTyrs] = useState([])
//для хранения избранных
const [favorites, setFavorites] = useState([])
//для хранения объектов корзины
const [cartItems, setCartItems] = useState([])

useEffect (()=>{
  async function axiosData(){
    const tyrsData = await axios.get('https://637f91ca2f8f56e28e904e7d.mockapi.io/tyrs')
    const favorotesData = await axios.get('https://637f91ca2f8f56e28e904e7d.mockapi.io/favorites')
    const cartData = await axios.get('https://637f91ca2f8f56e28e904e7d.mockapi.io/cart')

    setCartItems(cartData.data)
    setTyrs(tyrsData.data)
    setFavorites(favorotesData.data)
  }
  axiosData();
},[])


    const isFavorites = (myId)=>{
      return favorites.some((objIsFavorite)=> objIsFavorite.myId  === myId)
    }

    const isCart=(myId)=>{
      return cartItems.some((objIsCart)=> objIsCart.myId === myId)
    }

    const deleteItems=(id)=>{
      axios.delete(`https://637f91ca2f8f56e28e904e7d.mockapi.io/cart/${id}`)
      setCartItems((obj) => obj.filter(o => o.id !== id));
    }

  return (

    <AppContext.Provider
    value={
      {
        tyrs, 
        setTyrs,
        favorites,
        setFavorites,
        isFavorites,
        isCart,
        setCartItems
      }
    }
    >

    <div>

      <Router>
        <Header/>
          <Routes>

            <Route path='/favorites'
              element={
                <Favorites
                favorites = {favorites}
                setFavorites={setFavorites}
                cartItems={cartItems}
                />
              }
            />

          <Route path='/'
              element={
                <Home
                  item={tyrs}
                />
              }
            />

          <Route path='/form'
              element={
                <Form />
              }
            />

<Route path='/cart'
              element={
                < Basket
                deleteItems={deleteItems}
                />
              }
            />

        </Routes>
      </Router> 

      <Footer/>
    </div>
    </AppContext.Provider>
  );
}

export default App;
