import { Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from './components/Header';
import Footer from './components/Footer';

import "./App.css"
import { useState,useEffect} from 'react';
import axios from 'axios';
import Home from './components/Home';
import Form from './components/Form';
import {Route, Routes, BrowserRouter as Router} from 'react-router-dom'
import React from 'react';
import Favorites from './components/favorites/Favorites';

export const AppContext = React.createContext({})

function App() {
//хранения данных туров
const [tyrs, setTyrs] = useState([])
//
const [favorites, setFavorites] = useState([])

useEffect (()=>{
  async function axiosData(){
    const tyrsData = await axios.get('https://637f91ca2f8f56e28e904e7d.mockapi.io/tyrs')
    const favorotesData = await axios.get('https://637f91ca2f8f56e28e904e7d.mockapi.io/favorites')
    setTyrs(tyrsData.data)
    setFavorites(favorotesData.data)
  }
  axiosData();
},[])

  return (

    <AppContext.Provider
    value={
      {
        tyrs, 
        setTyrs,
        favorites,
        setFavorites
      }
    }
    >

    <div>

      <Router>
        <Header/>
          <Routes>

            <Route path='/favorotes'
              element={
                <Favorites/>
              }
            />

          <Route path='/'
              element={
                <Home
                  item={tyrs}
                />
              }
            />

          <Route path='/form'
              element={
                <Form />
              }
            />

        </Routes>
      </Router> 

      <Footer/>
    </div>
    </AppContext.Provider>
  );
}

export default App;
